package Com.Class2;

public class StringConcatenation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String name = "Hussain";

		String lastName = "Ahmad";

		char grade = 'A';

		String state = "Maryland";

		String city = "Elkridge";

		long phoneNumber = 2468906488L;

		System.out.println("My name is " + name + " and my lastname is " + lastName + ".");
		System.out.println("I am grade " + grade + " student" + ".");
		System.out.println("I live in city of " + city + " in a state of " + state + ".");
		System.out.println("My phone number is " + phoneNumber + ".");
		
		System.out.println ("My name is " + name + " and my last name is " +lastName+ " I have studied " +grade+ " and my grade is " +grade+
                " and I live in " + city + " " +state+ ".");
	}
 
}
